import React, { useState } from "react";
import axios from "axios";
import { BASE_URL } from "./apiConfig";
import { Button } from "react-bootstrap";

function CreateDish() {
  const [frm, setFrm] = useState(false)

  const [dishData, setDishData] = useState({
    dishName: "",
    description: "",
    price: "",
    dishImage: null, // Initialize dishImage as null
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "dishImage") {
      // Handle file input separately
      setDishData({
        ...dishData,
        dishImage: files[0], // Store the selected file object
      });
    } else {
      setDishData({
        ...dishData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create a FormData object to handle the file upload
      const formData = new FormData();
      formData.append("dishName", dishData.dishName);
      formData.append("description", dishData.description);
      formData.append("price", dishData.price);
      formData.append("dishImage", dishData.dishImage);

      const response = await axios.post(
        `${BASE_URL}/dishes/createDish`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data", // Set content type to multipart form data
          },
        }
      );

      console.log("Dish created:", response.data);
      // Handle success or update UI
    } catch (error) {
      console.error("Error creating dish:", error);
      // Handle error or show error message
    }
  };
  
  return (
    <div>
      <Button variant="primary" onClick={frm?setFrm(false):setFrm(true)}>Create Dish</Button>{' '}
      frm
    </div>
  );
}

export default CreateDish;
